const Icon = (props) => {
  const {
    id = "",
    name,
    color,
    viewBox = "0 0 100 100",
    className,
    ...rest
  } = props;

  return (
    <svg
      {...rest}
      xmlns="http://www.w3.org/2000/svg"
      viewBox={viewBox}
      aria-labelledby={`${name.replace(/\s/g, "").toLowerCase()}-${id}`}
      className={className || "icon"}
    >
      <title id={`${name.replace(/\s/g, "").toLowerCase()}-${id}`}>
        {name}
      </title>
      {getIcon(name, color)}
    </svg>
  );
};

